                if(allowing.indexOf('number') == -1)
                    allowing += ',number';

                if(allowing.indexOf('negative') > -1 && val.indexOf('-') === 0) {
                    val = val.substr(1);
                }

                if(allowing.indexOf('number') > -1 && val.replace(/[0-9]/g, '') === '') {
                    return true;
                }
